<script type="text/javascript">
$(".menu-open").click(function(){
    $(".menu-open").fadeToggle(100);
    $(".menu-close").delay(200).fadeToggle(500);
    $(".menu-mobile").slideToggle(800);
    $(".header").addClass('header-background');
});

$(".menu-close").click(function(){
    $(".menu-open").delay(200).fadeToggle(100);
    $(".menu-close").fadeToggle(500);
    $(".menu-mobile").slideToggle(800);
});
$(window).resize(function(){ if(window.innerWidth > 700) { $(".menu-mobile").removeAttr("style");}});

$(function(){var nav = $('.header');
    $(window).scroll(function () {
        if ($(this).scrollTop() > 10) {
            nav.addClass("header-background");
            $(".menu-close").click(function(){
                $(".header").addClass('header-background');
            });
        }
        else {
            nav.removeClass("header-background");
            $(".menu-close").click(function(){
                $(".header").removeClass('header-background');
            });
        }
    });
});
</script>